# run multiple notebooks with parameters
DAG = {
    "activities": [
        {
            "name": "nb1", # activity name, must be unique
            "path": "Notebook Arg 1", # notebook path
            "timeoutPerCellInSeconds": 90, # max timeout for each cell, default to 90 seconds
            "args": {"pInput1": 1, "pInput2": 2}, # notebook parameters
        },
        {
            "name": "nb2", # activity name, must be unique
            "path": "Notebook Arg 1", # notebook path
            "timeoutPerCellInSeconds": 90, # max timeout for each cell, default to 90 seconds
            "args": {"pInput1": 10, "pInput2": 20}, # notebook parameters
            "dependencies": ["nb1"] # list of activity names that this activity depends on
        }
    ]
}

mssparkutils.notebook.runMultiple(DAG)
